using System;
using System.Collections.Generic;
using System.Text;

namespace Domain.DatosBasicos
{
    public class DatosFincaSingleton
    {
        private DatosFincaSingleton() { }
        
        private static DatosFinca _datosFinca { get; set; }

        public static DatosFinca GetDatosFinca()
        {
            if(_datosFinca == null)
            {
                _datosFinca = new DatosFinca();
                _datosFinca.FincaNombre = "La Esperanza";
                _datosFinca.FincaLogo = "data:image/png;base64,";
                _datosFinca.PropietarioIdentificacion = "77564879";
                _datosFinca.PropietarioNombre = "Adalberto Molina Sanchez";
                _datosFinca.Telefono = "301 565 8789";
                _datosFinca.Direccion = "Km 43 via la paz, cesar";
            }

            return _datosFinca;
        }
    }

    public class DatosFinca
    {
        public string FincaLogo { get; set; }
        public string FincaNombre { get; set; }
        public string PropietarioIdentificacion { get; set; }
        public string PropietarioNombre { get; set; }
        public string Direccion { get; set; }
        public string Telefono { get; set; }
    }
}
